contracts/ProductRegistrationContract.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./UserRegistrationContract.sol";

contract ProductRegistrationContract {
    struct Product {
        string name;
        string metadataHash;
        address producer;
        bool isActive;
        uint256 custodyId;
    }

    mapping(uint256 => Product) public products;
    uint256 public nextProductId = 1;
    UserRegistrationContract public userContract;

    event ProductRegistered(
        uint256 indexed productId,
        uint256 indexed custodyId,
        string name,
        string metadataHash
    );
    event ProductUpdated(
        uint256 indexed productId,
        string name,
        string metadataHash
    );
    event ProductStatusChanged(
        uint256 indexed productId,
        bool isActive
    );
    event CustodyChanged(
        uint256 indexed productId,
        uint256 indexed oldCustodyId,
        uint256 indexed newCustodyId
    );

    constructor(address _userContract) {
        userContract = UserRegistrationContract(_userContract);
    }

    function registerProduct(
        string calldata name,
        string calldata metadataHash,
        uint256 initialCustodyId
    ) external returns (uint256) {
        require(bytes(name).length > 0, "Name required");
        require(bytes(metadataHash).length > 0, "Metadata hash required");

        uint256 id = nextProductId++;
        products[id] = Product({
            name: name,
            metadataHash: metadataHash,
            producer: msg.sender,
            isActive: true,
            custodyId: initialCustodyId
        });

        emit ProductRegistered(id, initialCustodyId, name, metadataHash);
        return id;
    }

    function updateProduct(
        uint256 id,
        string calldata name,
        string calldata metadataHash
    ) external {
        require(bytes(name).length > 0, "Name required");
        require(bytes(metadataHash).length > 0, "Metadata hash required");
        Product storage p = products[id];
        p.name = name;
        p.metadataHash = metadataHash;
        emit ProductUpdated(id, name, metadataHash);
    }

    function setProductActive(uint256 id, bool active) external {
        products[id].isActive = active;
        emit ProductStatusChanged(id, active);
    }

    function transferCustody(uint256 id, uint256 newCustodyId) external {
        Product storage p = products[id];
        uint256 old = p.custodyId;
        p.custodyId = newCustodyId;
        emit CustodyChanged(id, old, newCustodyId);
    }

    function getProduct(uint256 id)
        external
        view
        returns (
            string memory name,
            string memory metadataHash,
            address producer,
            bool isActive,
            uint256 custodyId
        )
    {
        Product storage p = products[id];
        return (p.name, p.metadataHash, p.producer, p.isActive, p.custodyId);
    }

    function getUserId(address who) public view returns (uint256) {
        return userContract.getUserId(who);
    }
}

contracts/SalesContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./UserRegistrationContract.sol";
import "./ProductRegistrationContract.sol";

contract SalesContract {
    UserRegistrationContract public userContract;
    ProductRegistrationContract public productContract;

    struct Sale {
        uint256 productId;
        uint256 fromCustody;
        uint256 toCustody;
        uint256 quantity;
        uint256 price;
        uint256 timestamp;
    }

    mapping(uint256 => Sale[]) public salesHistory;

    event ProductSold(
        uint256 indexed productId,
        uint256 indexed fromCustody,
        uint256 indexed toCustody,
        uint256 quantity,
        uint256 price,
        uint256 timestamp
    );

    constructor(address _userContract, address _productContract) {
        userContract = UserRegistrationContract(_userContract);
        productContract = ProductRegistrationContract(_productContract);
    }

    function sellProduct(
        uint256 productId,
        uint256 newCustodyId,
        uint256 quantity,
        uint256 price
    ) external {
        (, , , , uint256 currentCustody) = productContract.getProduct(productId);

        productContract.transferCustody(productId, newCustodyId);

        Sale memory s = Sale({
            productId:     productId,
            fromCustody:   currentCustody,
            toCustody:     newCustodyId,
            quantity:      quantity,
            price:         price,
            timestamp:     block.timestamp
        });
        salesHistory[productId].push(s);

        emit ProductSold(
            productId,
            currentCustody,
            newCustodyId,
            quantity,
            price,
            block.timestamp
        );
    }

    function getSalesCount(uint256 productId) external view returns (uint256) {
        return salesHistory[productId].length;
    }

    function getSale(uint256 productId, uint256 index)
        external
        view
        returns (Sale memory)
    {
        return salesHistory[productId][index];
    }

    function getUserId(address who) public view returns (uint256) {
        return userContract.getUserId(who);
    }
}

contracts/ShipmentRegistrationContract.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract ShipmentRegistrationContract is AccessControl {
    bytes32 public constant DISTRIBUTOR_ROLE = keccak256("DISTRIBUTOR_ROLE");

    enum Status { Pending, InTransit, Confirmed, Cancelled }

    struct Shipment {
        uint256   productId;
        address   distributor;
        string    origin;
        string    destination;
        string    transportCompany;
        uint256   quantity;
        string    notes;
        Status    status;
        uint256   createdAt;
    }

    mapping(uint256 => Shipment) public shipments;
    uint256 public nextShipmentId;

    event ShipmentCreated(
        uint256 indexed shipmentId,
        uint256 productId,
        address indexed distributor,
        string origin,
        string destination,
        string transportCompany,
        uint256 quantity,
        string notes
    );

    event ShipmentStatusUpdated(
        uint256 indexed shipmentId,
        Status newStatus
    );

    constructor() {
        // En OZ ≥4.9, _setupRole ya no existe: usamos _grantRole
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function grantDistributor(address who) external onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(DISTRIBUTOR_ROLE, who);
    }
    function revokeDistributor(address who) external onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(DISTRIBUTOR_ROLE, who);
    }

    function createShipment(
        uint256 productId,
        string calldata origin,
        string calldata destination,
        string calldata transportCompany,
        uint256 quantity,
        string calldata notes
    )
        external
        onlyRole(DISTRIBUTOR_ROLE)
        returns (uint256)
    {
        require(quantity > 0, "Quantity>0");
        uint256 id = nextShipmentId++;
        shipments[id] = Shipment({
            productId:        productId,
            distributor:      msg.sender,
            origin:           origin,
            destination:      destination,
            transportCompany: transportCompany,
            quantity:         quantity,
            notes:            notes,
            status:           Status.InTransit,
            createdAt:        block.timestamp
        });
        emit ShipmentCreated(
          id, productId, msg.sender, origin, destination, transportCompany, quantity, notes
        );
        return id;
    }

    function updateStatus(uint256 shipmentId, Status newStatus)
        external
        onlyRole(DISTRIBUTOR_ROLE)
    {
        Shipment storage s = shipments[shipmentId];
        require(s.distributor == msg.sender,  "Not your shipment");
        require(s.status == Status.InTransit, "Must be in_transit");
        require(
            newStatus == Status.Confirmed ||
            newStatus == Status.Cancelled,
            "Bad status"
        );
        s.status = newStatus;
        emit ShipmentStatusUpdated(shipmentId, newStatus);
    }

    function getShipment(uint256 shipmentId)
        external
        view
        returns (Shipment memory)
    {
        return shipments[shipmentId];
    }
}

contracts/UserRegistrationContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract UserRegistrationContract {
    struct User {
        string  name;
        string  email;
        bool    isActive;
        uint256 userId;
    }

    mapping(address => User) public users;
    address public owner;
    uint256 public nextUserId = 1;

    event UserRegistered(address indexed userAddress, string name, string email, uint256 userId);
    event UserUpdated(address indexed userAddress, string name, string email);
    event UserDeactivated(address indexed userAddress);

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can perform this action");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function registerUser(address _userAddress, string memory _name, string memory _email)
        public onlyOwner
    {
        require(!users[_userAddress].isActive, "User already registered");
        uint256 uid = nextUserId++;
        users[_userAddress] = User({
            name:     _name,
            email:    _email,
            isActive: true,
            userId:   uid
        });
        emit UserRegistered(_userAddress, _name, _email, uid);
    }

    function updateUser(string memory _name, string memory _email) public {
        require(users[msg.sender].isActive, "User is not active");
        users[msg.sender].name  = _name;
        users[msg.sender].email = _email;
        emit UserUpdated(msg.sender, _name, _email);
    }

    function deactivateUser(address _userAddress) public onlyOwner {
        require(users[_userAddress].isActive, "User is not active");
        users[_userAddress].isActive = false;
        emit UserDeactivated(_userAddress);
    }

    function isUserRegistered(address _userAddress) public view returns (bool) {
        return users[_userAddress].isActive;
    }

    function getUserId(address who) public view returns (uint256) {
        require(users[who].isActive, "User not registered");
        return users[who].userId;
    }
}

